import React, { useState, useEffect } from 'react';
import { getLists, deleteList, updateList } from '../utils/api';
import ListItem from './ListItem';
import './ListsPage.css';

const ListsPage = () => {
  const [lists, setLists] = useState([]);
  const [editingList, setEditingList] = useState(null);
  const [newListName, setNewListName] = useState('');

  useEffect(() => {
    const fetchLists = async () => {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await getLists();
      setLists(response.data.filter((list) => list.userId === user._id));
    };
    fetchLists();
  }, []);

  const handleEditList = (list) => {
    setEditingList(list);
    setNewListName(list.name);
  };

 
  const handleSaveList = async () => {
    try {
      const response = await updateList(editingList._id, {
        name: newListName,
        responseCodes: editingList.responseCodes
      });
      setLists(
        lists.map((list) =>
          list._id === response.data._id ? response.data : list
        )
      );
      setEditingList(null);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeleteList = async (list) => {
    try {
      await deleteList(list._id);
      setLists(lists.filter((l) => l._id !== list._id));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="lists-page">
      <h1>Lists Page</h1>
      <div className="lists-page__list">
        {lists.map((list) => (
          <ListItem
            key={list._id}
            list={list}
            isEditing={editingList?._id === list._id}
            onEdit={handleEditList}
            onSave={handleSaveList}
            onDelete={handleDeleteList}
            newListName={newListName}
            setNewListName={setNewListName}
          />
        ))}
      </div>
    </div>
  );
};

export default ListsPage;
