import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { getResponseCodes, addResponseCode } from '../utils/api';
import './SearchPage.css';

const SearchPage = () => {
  const [responseCodes, setResponseCodes] = useState([]);
  const [filteredCodes, setFilteredCodes] = useState([]);
  const [newCode, setNewCode] = useState('');
  const [newImage, setNewImage] = useState('');
  const [filter, setFilter] = useState('');

  useEffect(() => {
    const fetchResponseCodes = async () => {
      try {
        const response = await getResponseCodes();
        setResponseCodes(response.data);
        filterResponseCodes(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchResponseCodes();
  }, []);

  const getImageUrl = async (code) => {
    try {
      const response = await axios.get(`https://http.dog/${code}`);
      return response.data;
    } catch (error) {
      console.error(error);
      return null;
    }
  };

  const filterResponseCodes = async (codes) => {
    let filteredCodes = codes;
    if (filter === '2xx') {
      filteredCodes = await Promise.all(
        codes.filter(code => code.code.startsWith('2')).map(async code => ({
          ...code,
          image: await getImageUrl(code.code)
        }))
      );
    } else if (filter === '20x') {
      filteredCodes = await Promise.all(
        codes.filter(code => code.code.startsWith('20')).map(async code => ({
          ...code,
          image: await getImageUrl(code.code)
        }))
      );
    } else if (filter === '21x') {
      filteredCodes = await Promise.all(
        codes.filter(code => code.code.startsWith('21')).map(async code => ({
          ...code,
          image: await getImageUrl(code.code)
        }))
      );
    } else if (filter === '3xx') {
      filteredCodes = await Promise.all(
        codes.filter(code => code.code.startsWith('3')).map(async code => ({
          ...code,
          image: await getImageUrl(code.code)
        }))
      );
    } else {
      filteredCodes = await Promise.all(
        codes.filter(code => code.code === filter).map(async code => ({
          ...code,
          image: await getImageUrl(code.code)
        }))
      );
    }
    setFilteredCodes(filteredCodes);
  };

  const handleFilterChange = (e) => {
    const newFilter = e.target.value;
    setFilter(newFilter);
    filterResponseCodes(responseCodes);
  };

  const handleAddResponseCode = async () => {
    try {
      await addResponseCode({ code: newCode, image: newImage });
      setNewCode('');
      setNewImage('');
      // Refetch the response codes
      const response = await getResponseCodes();
      setResponseCodes(response.data);
      filterResponseCodes(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="search-page">
      <h1>HTTP Response Codes</h1>
      <input
        type="text"
        placeholder="Filter by code (e.g., 2xx, 20x, 21x, 3xx, or specific code)"
        value={filter}
        onChange={handleFilterChange}
        className="search-page__input"
      />
      <div className="search-page__results">
        {filteredCodes.map(responseCode => (
          <div key={responseCode.code} className="search-page__result">
            <span className="search-page__code">{responseCode.code}</span>
            <img src={responseCode.image} alt={`HTTP ${responseCode.code}`} className="search-page__image" />
          </div>
        ))}
      </div>
      <div className="search-page__form">
        <input
          type="text"
          placeholder="Response Code"
          value={newCode}
          onChange={e => setNewCode(e.target.value)}
          className="search-page__input"
        />
        <input
          type="text"
          placeholder="Image URL"
          value={newImage}
          onChange={e => setNewImage(e.target.value)}
          className="search-page__input"
        />
        <button onClick={handleAddResponseCode} className="search-page__button">
          Add Response Code
        </button>
      </div>
    </div>
  );
};

export default SearchPage;
