import React, { useState } from 'react';
import axios from 'axios';
import './SearchPage.css';

import { useNavigate } from 'react-router-dom';

const SearchPage = () => {
  const [searchCode, setSearchCode] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [filter, setFilter] = useState('');
  const [filteredCodes, setFilteredCodes] = useState([]);
  const navigate = useNavigate();

  const handleSearch = async () => {
    try {
      const response = await axios.get(`https://http.dog/${searchCode}`);
      setSearchResult(response.data);
    } catch (error) {
      console.error(error);
      setSearchResult(null);
    }
  };

  const handleFilterChange = (e) => {
    const newFilter = e.target.value;
    setFilter(newFilter);
    filterResponseCodes(newFilter);
  };

  const filterResponseCodes = (filter) => {
    let filtered = [];
    if (filter === '2xx') {
      filtered = filteredCodes.filter(code => code.startsWith('2'));
    } else if (filter === '20x') {
      filtered = filteredCodes.filter(code => code.startsWith('20'));
    } else if (filter === '21x') {
      filtered = filteredCodes.filter(code => code.startsWith('21'));
    } else if (filter === '3xx') {
      filtered = filteredCodes.filter(code => code.startsWith('3'));
    } else {
      filtered = filteredCodes.filter(code => code === filter);
    }
    setFilteredCodes(filtered);
  };

  const handleSaveList = async () => {
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      await axios.post('/api/lists', {
        name: 'New List',
        responseCodes: filteredCodes,
        userId: user._id
      });
      navigate('/lists');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="search-page">
      <h1>Search HTTP Response Codes</h1>
      <div className="search-page__input-container">
        <input
          type="text"
          placeholder="Enter response code"
          value={searchCode}
          onChange={(e) => setSearchCode(e.target.value)}
          className="search-page__input"
        />
        <button onClick={handleSearch} className="search-page__button">
          Search
        </button>
      </div>
      {searchResult && (
        <div className="search-page__result">
          <img src={searchResult} alt={`HTTP ${searchCode}`} className="search-page__image" />
        </div>
      )}
      <input
        type="text"
        placeholder="Filter by code (e.g., 2xx, 20x, 21x, 3xx, or specific code)"
        value={filter}
        onChange={handleFilterChange}
        className="search-page__input"
      />
      <div className="search-page__results">
        {filteredCodes.map((code, index) => (
          <div key={index} className="search-page__result">
            <span className="search-page__code">{code}</span>
            <img src={`https://http.dog/${code}`} alt={`HTTP ${code}`} className="search-page__image" />
          </div>
        ))}
      </div>
      <button onClick={handleSaveList} className="search-page__button">
        Save List
      </button>
    </div>
  );
};

export default SearchPage;
